    def ActionButton(self):
        text = self.textSentence.toPlainText()
        emotions = {}
        if len(text) < 1 or len(text) > 280:
            print("Escriba un texto de 280 caracteres como máximo.")
            return
        if self.rBert.isChecked() | self.rNRCL.isChecked():
            if self.rNRCL.isChecked():
                emotions = process.processNRCLexer(text)
                if emotions:
                    self.updateLabelsNRC(emotions)
            if self.rBert.isChecked():
                emotions = process.processTransformer(text)
                if emotions:
                    self.updateLabelsBERT(emotions)
        else:
            print('No se ha elegido un método de detección')

    def updateLabelsNRC(self, emotions):
        self.positiveLabel.setText(max(emotions['positive'], key=emotions['positive'].get))
        self.negativeLabel.setText(max(emotions['negative'], key=emotions['negative'].get))
        self.joyLabel.setText(max(emotions['joy'], key=emotions['joy'].get))
        self.trustLabel.setText(max(emotions['trust'], key=emotions['trust'].get))
        self.surpriseLabel.setText(max(emotions['surprise'], key=emotions['surprise'].get))
        self.anticipationLabel.setText(max(emotions['anticipation'], key=emotions['anticipation'].get))
        self.sadnessLabel.setText(max(emotions['sadness'], key=emotions['sadness'].get))
        self.disgustLabel.setText(max(emotions['disgust'], key=emotions['disgust'].get))
        self.fearLabel.setText(max(emotions['fear'], key=emotions['fear'].get))
        self.angerLabel.setText(max(emotions['anger'], key=emotions['anger'].get))

    def updateLabelsBERT(self, emotions):
        emo_labels = {
            'fear': '-', 'anger': '-', 'anticipation': '-',
            'trust': '-', 'surprise': '-', 'positive': '-',
            'negative': '-', 'sadness': '-', 'disgust': '-',
            'joy': '-'
        }
        bert_label = str(emotions['label'])
        if emo_labels.get(bert_label):
            bert_label = str(emotions['label'])
            emo_labels[bert_label] = '✔️'

        self.positiveLabel.setText(emo_labels['positive'])
        self.negativeLabel.setText(emo_labels['negative'])
        self.joyLabel.setText(emo_labels['joy'])
        self.trustLabel.setText(emo_labels['trust'])
        self.surpriseLabel.setText(emo_labels['surprise'])
        self.anticipationLabel.setText(emo_labels['anticipation'])
        self.sadnessLabel.setText(emo_labels['sadness'])
        self.disgustLabel.setText(emo_labels['disgust'])
        self.fearLabel.setText(emo_labels['fear'])
        self.angerLabel.setText(emo_labels['anger'])

